example.programlisting:
{foreach name=outer item=contact from=$contacts}
  <hr />
  {foreach key=key item=item from=$contact}
    {$key}: {$item}<br />
  {/foreach}
{/foreach}

{foreach key=cid item=con from=$results}
    <a href="contact.php?contact_id={$cid}">{$con.name} - {$con.nick}</a><br />
{foreachelse}
    No items were found in the search
{/foreach}

{* The header block is output every five rows *}
<table>
{foreach from=$items item=i name=foo}
  {if $smarty.foreach.foo.index % 5 == 0}
     <tr><th>Title</th></tr>
  {/if}
  <tr><td>{$i.label}</td></tr>
{/foreach}
</table>

{* The header block is output every five rows *}
<table>
{foreach from=$items item=i}
  {if $i@index % 5 == 0}
     <tr><th>Title</th></tr>
  {/if}
  <tr><td>{$i.label}</td></tr>
{/foreach}
</table>

{* this will output 0|1, 1|2, 2|3, ... etc *}
{foreach from=$myArray item=i name=foo}
{$smarty.foreach.foo.index}|{$smarty.foreach.foo.iteration},
{/foreach}

{* this will output 0|1, 1|2, 2|3, ... etc *}
{foreach from=$myArray item=i}
{$i@index}|{$i@iteration},
{/foreach}

{* show LATEST on the first item, otherwise the id *}
<table>
{foreach from=$items key=myId item=i name=foo}
<tr>
  <td>{if $smarty.foreach.foo.first}LATEST{else}{$myId}{/if}</td>
  <td>{$i.label}</td>
</tr>
{/foreach}
</table>

{* show LATEST on the first item, otherwise the id *}
<table>
{foreach from=$items item=i}
<tr>
  <td>{if $i@first}LATEST{else}{$i@key}{/if}</td>
  <td>{$i.label}</td>
</tr>
{/foreach}
</table>

{* Add horizontal rule at end of list *}
{foreach from=$items key=part_id item=prod name=products}
  <a href="#{$part_id}">{$prod}</a>{if $smarty.foreach.products.last}<hr>{else},{/if}
{foreachelse}
  ... content ...
{/foreach}

{* Add horizontal rule at end of list *}
{foreach from=$items key=part_id item=prod}
  <a href="#{$part_id}">{$prod}</a>{if $prod@last}<hr>{else},{/if}
{foreachelse}
  ... content ...
{/foreach}

{* show rows returned at end *}
{foreach from=$items key=part_id item=prod name=foo}
{$prod.name}<hr/>
{if $smarty.foreach.foo.last}
  <div id="total">{$smarty.foreach.foo.total} items</div>
{/if}
{foreachelse}
 ... something else ...
{/foreach}

{* show rows returned at end *}
{foreach from=$items key=part_id item=prod}
{$prod.name}<hr/>
{if $prod@last}
  <div id="total">{$prod@total} items</div>
{/if}
{foreachelse}
 ... something else ...
{/foreach}
